import './App.css';
import View_Stacks from './Components/ViewStacks';

/**************
App.js composed of :

1) State for all aspects of app
2) App Render
**************/


class App extends Component {



  state = {
    stacks : [
      {stack :
        {
          streak:0,
          habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        },
        name : "Morning Routine",
        isOpen : true,
      },
      {stack :
        {
          streak:0,
          habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        },
        name : "Evening Routine",
        isOpen : true,
      },

    ],
    makingNewHabit : false,
    inputtingNewHabit : false,
    newHabit : {
      cue:"then",
      action:"",
      itemPos:null,
      stackID:null,
    }
  }



  toggleStack = (id) => {

    // let oldToggle = this.state.stacks[id].isOpen;
    // let newToggle = !oldToggle;
    // console.log("newT", newToggle);
    // this.setState({oldToggle : newToggle});

    let s = this.state.stacks[id];
    let oldStack = s;
    let newStack = s;
    newStack.isOpen = false;
    console.log('ne ', newStack.isOpen);
  }





  render() {
    return (
      <div className="App">
        <View_Stacks stacks={this.state.stacks} toggleStack={this.toggleStack}/>
      </div>
    );
  }
}

export default App;
