import React, { Component } from 'react';
import './App.css';
import ViewStacks from './Components/ViewStacks';
import AnimateHeight from 'react-animate-height';
import {arrayMove} from 'react-sortable-hoc';
import styled, {createGlobalStyle} from 'styled-components';

/**************
TO DO

- remove unneeded fonts when style is done

Note:
array of habits (stacks[]) is separate from array of stack info (stacksInfo[])
due to how Sortable library works with arrys not objects. Unless I go into their
library and make some changes I'm stuck with this setup I believe.
This is technical debt that may hurt later.
https://github.com/clauderic/react-sortable-hoc

Note:
Never figured out importing local font files. Might have something to do with needing
to add a loader to webpack

**************/

const AppStyled = styled.div`
  position:relative;
  width:100%;
  max-width: 500px;
  margin: 0 auto;
  display: block;

  @media(min-width:769px){
    .App {
      margin-top: 40px;
    }
  }
`
const Typography = createGlobalStyle`
  @import url('https://fonts.googleapis.com/css?family=Poppins:700');
  @import url('https://fonts.googleapis.com/css?family=Roboto:300i,400,500');
`;
const GlobalStyles = createGlobalStyle`
  h3 {
    font-family : 'Poppins';
    font-weight : 700;
  }
  body {
    font-family: 'Roboto';
    font-weight: 400;
  }
  html {
    font-size: 16px;
    overflow: scroll;
    overflow-x: hidden;
  }
  ::-webkit-scrollbar {
    width: 0px;  /* remove scrollbar space */
    background: transparent;  /* optional: just make scrollbar invisible */
  }
`;



class App extends Component {



  state = {

    stacks : [
      [
        {
          action : "Meditate 15mins",
          cue: "7:00am",
          state: "neutral",
        },
        {
          action:"Exercise",
          cue:"then",
          state: "neutral",
        },
        {
          action:"Shower",
          cue:"then",
          state: "neutral",
        },
      ],
      [ {action:"Meditate 15mins", cue:"7:00am", state: "neutral"},{action:"Exercise", cue:"then", state: "neutral"},{action:"Shower", cue:"then", state: "neutral"}, ],
      [ {action:"Meditate 15mins", cue:"7:00am", state: "neutral"},{action:"Exercise", cue:"then", state: "neutral"},{action:"Shower", cue:"then", state: "neutral"}, ],

    ],
    stacksInfo : [
      { name : "Morning Routine", streak: 10, height : "auto", },
      { name : "After School ", streak: 4, height : "auto", },
      { name : "Night Routine", streak: 9, height : "auto", },
    ],


  };



  toggleStack = (id) => {

    let oldStack = this.state.stacksInfo[id];
    let newStack = oldStack;

    newStack.height = oldStack.height === 0 ? 'auto' : 0;

    this.setState({
      oldStack : newStack
    })
  }
  logHabit = () => {
    console.log("log works");
  }
  onSortEnd = ({oldIndex, newIndex, collection}) => {
    this.setState(({stacks}) => {
      const newstacks = [...stacks];

      newstacks[collection] = arrayMove(
        stacks[collection],
        oldIndex,
        newIndex,
      );
      return {stacks: newstacks};
    });
  };

  render() {
    return (
      <AppStyled>

        <Typography />
        <GlobalStyles />

        <ViewStacks
          stacks={this.state}
          stacksInfo={this.state.stacksInfo}
          toggleStack={this.toggleStack}
          logHabit={this.logHabit}
          onSortEnd={this.onSortEnd}
        />

      </AppStyled>

    );
  }
}

export default App;
