const StackName = styled.h3`
  font-size: 1.625rem;
  display: inline-block;
  padding-left: 10px;
  color:#4E4E4E;
`


function Stack(props){

  return (

    <div className="stack" key={props.stacksIndex}>
      <div className="stackHeader">
        <div className="toggle" onClick={() => props.toggleStack(props.stacksIndex)}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>
        <StackName>{props.stacksInfo[props.stacksIndex].name}</StackName>
      </div>
      <AnimateHeight
        duration={ 500 }
        key={props.id}
        height={ props.height }
      >
          <ul className="stackBody">
            {props.stacksItems.map((item, i) => (
              <Habit
                key={i}
                value={item}
                index={i}
                collection={props.stacksIndex}
              />
            ))}
          </ul>
      </AnimateHeight >
      <div className="stackBottom">
        <div className="stackLine"></div>
        <p className="streakName">Streak</p>
        <p className="streakCount">{props.stacksInfo[props.stacksIndex].streak}</p>
      </div>
    </div>

  )
}

export default Stack;
