import React from 'react';
import styled from 'styled-components';
import { Formik, Field, Form } from 'formik';

const Module = styled.div`
  position:absolute;
  width: 100%;
  height: 100%;
  z-index: 10;
  background-color:#fff;
  transition: right .2s ease;
  top:0;
  right: ${props => props.active ? "0" : "100%"}
`

const Cancel = styled.button`
  background: #F3F3F3;
  margin-right: 10px;
  position: relative;
  margin-left: auto;
  margin-top: 10px;
`

function AddHabitModule(props){
  return (
    <Module active={props.activeStates.addModuleIsActive}>
      <Cancel onClick={props.cancelHabitModule}> Cancel </Cancel>
      <Formik>
      <Formik
        initialValues={user /** { email, social } */}
        onSubmit={(values, actions) => {
          MyImaginaryRestApiCall(user.id, values).then(
            updatedUser => {
              actions.setSubmitting(false);
              updateUser(updatedUser);
              onClose();
            },
            error => {
              actions.setSubmitting(false);
              actions.setErrors(transformMyRestApiErrorsToAnObject(error));
              actions.setStatus({ msg: 'Set some arbitrary status or data' });
            }
          );
        }}
        render={({ errors, status, touched, isSubmitting }) => (
          <Form>
            <Field type="email" name="email" />
            {errors.email && touched.email && <div>{errors.email}</div>}
            <Field type="text" name="social.facebook" />
            {errors.social && errors.social.facebook &&
              touched.social.facebook && <div>{errors.social.facebook}</div>}
            <Field type="text" name="social.twitter" />
            {errors.social && errors.social.twitter &&
              touched.social.twitter && <div>{errors.social.twitter}</div>}
            {status && status.msg && <div>{status.msg}</div>}
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )}
      />
      </Formik>
    </Module>
  )
}


export default AddHabitModule;
