//     let habitsInJSX = [];
//     stack.forEach((habit, i) => {
//       habitsInJSX.push(
//         <Habit
//           action={habit.action}
//           cue={habit.cue}
//           key={i}/>
//       )
//     });
//     return habitsInJSX;
//   }
//   else {
//     return <p>Tap + to add a habit</p>
//   }
// }


const StackName = styled.h3`
  font-size: 1.625rem;
  display: inline-block;
  padding-left: 10px;
  color:#4E4E4E;
`
const Toggle = styled.div`
  display: inline-block;
  vertical-align: text-bottom;
  width: 3.125rem;
  height: 3rem;
  border-radius: 5px;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.15);
  background-color: #ffffff;
  padding-top:2px;
  display: inline-flex;
  justify-content: center;
  align-items: center;
`

const BarContainer = styled.div`
  height: 1.25rem;
  width: 1.625rem;
  /* margin: 0.8125rem auto 0 auto; */
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`
const Bar = styled.div`
  width: 26px;
  height: 2px;
  background-color: #3D3D3D
`

function Stack(props){

  return (

    <div className="stack" key={props.stacksIndex}>
      <div className="stackHeader">
        <Toggle onClick={() => props.toggleStack(props.stacksIndex)}>
          <BarContainer>
            <Bar />
            <Bar />
            <Bar />
          </BarContainer>
        </Toggle>
        <StackName>{props.stacksInfo[props.stacksIndex].name}</StackName>
      </div>
      <AnimateHeight
        duration={ 500 }
        key={props.id}
        height={ props.height }
      >
          <ul className="stackBody">
            {props.stacksItems.map((item, i) => (
              <Habit
                key={i}
                value={item}
                index={i}
                collection={props.stacksIndex}
              />
            ))}
          </ul>
      </AnimateHeight >
      <div className="stackBottom">
        <div className="stackLine"></div>
        <p className="streakName">Streak</p>
        <p className="streakCount">{props.stacksInfo[props.stacksIndex].streak}</p>
      </div>
    </div>

  )
}

export default Stack;
