import React from 'react';
import styled from 'styled-components';
import '../App.css';
import {sortableElement} from 'react-sortable-hoc';

const Li = styled.li`
  height:60px;
  margin-top: 25px;
  margin-bottom: 25px;
  list-style: none;
`

const Circle = styled.div`
  display:inline-block;
  margin: 20px 0px;
  height:16px;
  width:16px;
  border-radius:30px;
  border: solid 1px #333;
  transition: box-shadow .3s;
  transition: background-color .2s;
`

const HabitText = styled.div`
  display: inline-block;
  margin-left: 20px;
  vertical-align: top;
`

const Cue = styled.div`
  font-family: Roboto;
  font-weight: 300;
  font-size: 18px;
  color: #A6A6A6;
  letter-spacing: 0;
  line-height: 30px;
`

const Action = styled.div`
  font-family: Roboto;
  font-weight: 500;
  font-size: 18px;
  color: #3D3D3D;
  letter-spacing: 0;
  line-height: 30px;
`

const SortableItem = sortableElement(
  ({value}) => (
    <Li>
      <Circle />
      <HabitText>
        <Cue>{value.cue}</Cue>
        <Action>{value.action}</Action>
      </HabitText>
    </Li>
  )
);


function Habit(props){
  return (
    <SortableItem
      value={props.value}
      index={props.index}
      collection={props.collection}
    />
  )
}


export default Habit;
