import React, { Component } from 'react';
import './App.css';
import View_Stacks from './Components/ViewStacks';
import AnimateHeight from 'react-animate-height';

/**************
App.js composed of :

1) State for all aspects of app
2) App Render
**************/


class App extends Component {



  state = {
    stacks : [
      {stack :
        {
          streak:0,
          habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        },
        name : "Morning Routine",
        isOpen : true,
        height : "auto",
      },
      {stack :
        {
          streak:0,
          habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        },
        name : "Evening Routine",
        isOpen : true,
        height : "auto",
      },

    ],
    makingNewHabit : false,
    inputtingNewHabit : false,
    newHabit : {
      cue:"then",
      action:"",
      itemPos:null,
      stackID:null,
    }
  }



  toggleStack = (id) => {

    // let oldStack = this.state.stacks[id];
    // let newStack = oldStack;
    // newStack.isOpen = !oldStack.isOpen;
    //
    // this.setState({
    //   oldStack : newStack
    // })

    const stack = this.state.stacks[id].isOpen;
    this.setState({
      stack : !stack.isOpen
    })

    let height = this.state.stacks[id].height;

    this.setState({
      height: height === 0 ? 'auto' : 0,
    });
  }





  render() {
    return (
      <div className="App">
        <View_Stacks stacks={this.state.stacks} toggleStack={this.toggleStack}/>
      </div>
    );
  }
}

export default App;
