import React, { Component } from 'react';
import '../App.css';
import Habit from './Habit';


function displayHabits(stack){

  console.log("stack is ", stack);
  if (stack.length !== 0) {
    let habitsInJSX = [];
    stack.forEach((habit, i) => {
      habitsInJSX.push(
        <Habit name={habit.action} key={i}/>
      )
    });
    return habitsInJSX;
  }
  else {
    return <p>Tap + to add a habit</p>
  }
}


function Stack(props){
  return (
    <div className="stack">
      <div className="stackHeader">
        <div className="toggle">
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>
        <h3 className="stackName">{props.name}!</h3>
      </div>
      <div className="stackBody">
        {displayHabits(props.habits)}
      </div>
    </div>
  )
}

export default Stack;
