import React, { Component } from 'react';
import './App.css';
import View_Stacks from './Components/ViewStacks';
import AnimateHeight from 'react-animate-height';
import {arrayMove} from 'react-sortable-hoc';
import TestArea from './Components/TestArea';

/**************
App.js composed of :

1) State for all aspects of app
2) App Render
**************/


class App extends Component {



  state = {
    test : [1, 2, 3, 4, 5],
    stacks : [
      { habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        streak: 0,
        name : "Morning Routine",
        height : "auto",
      },
      { habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        streak:2,
        name : "Evening Routine",
        height : "auto",
      },

    ],
    makingNewHabit : false,
    inputtingNewHabit : false,
    newHabit : {
      cue:"then",
      action:"",
      itemPos:null,
      stackID:null,
    }
  };



  toggleStack = (id) => {

    let oldStack = this.state.stacks[id];
    let newStack = oldStack;

    newStack.height = oldStack.height === 10 ? 'auto' : 10;

    this.setState({
      oldStack : newStack
    })
  }

  onSortEnd = ({oldIndex, newIndex}) => {

  let itemArr = this.state.stacks[0].habits;

    this.setState(({itemArr}) => ({
      itemArr : arrayMove(itemArr, oldIndex, newIndex),
    }));
  };


  render() {
    return (
      <div className="App">

        <View_Stacks
          stacks={this.state.stacks}
          toggleStack={this.toggleStack}
          onSortEnd={this.onSortEnd}
          />
          <TestArea />

      </div>
    );
  }
}

export default App;
