import React from 'react';
import '../App.css';
import Habit from './Habit';

/**************
Stack Component

is it bad I affect the DOM with the toggleStack() ?
**************/

function displayHabits(stack){

  if (stack.length !== 0) {
    let habitsInJSX = [];
    stack.forEach((habit, i) => {
      habitsInJSX.push(
        <Habit
          action={habit.action}
          cue={habit.cue}
          key={i}/>
      )
    });
    return habitsInJSX;
  }
  else {
    return <p>Tap + to add a habit</p>
  }
}


let visible = "visible";

function toggleStack(id){
  // document.querySelector(`stack-${id}`).classList.toggle('stackVisible')
}

function Stack(props){
  return (
    <div className={`stack ${visible}`}>
      <div className="stackHeader">
        <div className="toggle" onClick={()=>{toggleStack(props.id)}}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>
        </div>
        <h3 className="stackName">{props.name}!</h3>
      </div>
      <div className="stackBody">
        {displayHabits(props.habits)}
      </div>
    </div>
  )
}

export default Stack;
