import React, { Component } from 'react';
import './App.css';
import View_Stacks from './Components/ViewStacks';
// import { CSSTransitionGroup } from 'react-transition-group' // ES6

 var CSSTransitionGroup = require('react-transition-group/CSSTransitionGroup') // ES5 with npm

/**************
App.js composed of :

1) State for all aspects of app
2) App Render
**************/


class App extends Component {



  state = {
    stacks : [
      {stack :
        {
          streak:0,
          habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        },
        name : "Morning Routine",
        isOpen : true,
      },
      {stack :
        {
          streak:0,
          habits:[
            {cue:"7:00AM", action:"jog", result:"blank", fn:""},
            {cue:"then", action:"shower", result:"blank", fn:""},
            {cue:"then", action:"breakfast", result:"blank", fn:""},
          ],
        },
        name : "Evening Routine",
        isOpen : true,
      },

    ],
    makingNewHabit : false,
    inputtingNewHabit : false,
    newHabit : {
      cue:"then",
      action:"",
      itemPos:null,
      stackID:null,
    }
  }



  toggleStack = (id) => {

    let oldStack = this.state.stacks[id];
    let newStack = oldStack;
    newStack.isOpen = !oldStack.isOpen;

    this.setState({oldStack : newStack})
  }





  render() {
    return (
      <div className="App">
        <CSSTransitionGroup
          transitionName="example"
          transitionEnterTimeout={500}
          transitionLeaveTimeout={300}>
          <View_Stacks stacks={this.state.stacks} toggleStack={this.toggleStack}/>
        </CSSTransitionGroup>

      </div>
    );
  }
}

export default App;
