//         <Habit
//           action={habit.action}
//           cue={habit.cue}
//           key={i}/>
//       )
//     });
//     return habitsInJSX;
//   }
//   else {
//     return <p>Tap + to add a habit</p>
//   }
// }


const StackName = styled.h3`
  font-size: 1.625rem;
  display: inline-block;
  padding-left: 1.25rem;
  color:#4E4E4E;
`
const Toggle = styled.div`
  display: inline-block;
  vertical-align: middle;
  margin-bottom: 10px;
  width: 3.125rem;
  height: 3rem;
  border-radius: 5px;
  box-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.15);
  background-color: #ffffff;
  padding-top:2px;
  display: inline-flex;
  justify-content: center;
  align-items: center;
`

const BarContainer = styled.div`
  height: 1.25rem;
  width: 1.625rem;
  /* margin: 0.8125rem auto 0 auto; */
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`
const Bar = styled.div`
  width: 26px;
  height: 2px;
  background-color: #3D3D3D
`

const StackLine = styled.div`
  display:inline-block;
  width: 100%;
  width: calc(100% - 3.125rem);
  height: 2px;
  margin-bottom: -8px;
  background-color: #d7d7d7;
`

const StreakCount = styled.div`
  display:inline-flex;
  justify-content:center;
  align-items:center;
  height: 3.25rem;
  width: 3.25rem;
  background: #FFFFFF;
  box-shadow: 0 0 6px 0 rgba(0,0,0,0.14);
  border-radius: 99px;
  font-family: Poppins;
  font-weight:700;
  font-size: 1.3rem;
  color: #656464;
`

const StackBody = styled.ul`

`

function Stack(props){

  return (

    <div className="stack" key={props.stacksIndex}>
      <div className="stackHeader">
        <Toggle onClick={() => props.toggleStack(props.stacksIndex)}>
          <BarContainer>
            <Bar />
            <Bar />
            <Bar />
          </BarContainer>
        </Toggle>
        <StackName>{props.stacksInfo[props.stacksIndex].name}</StackName>
      </div>

      <AnimateHeight  duration={ 500 } key={props.id} height={ props.height } >
          <StackBody>
            {props.stacksItems.map((item, i) => (
              <Habit
                key={i}
                value={item}
                index={i}
                collection={props.stacksIndex}
              />
            ))}
          </StackBody>
      </AnimateHeight >
      <div className="stackBottom">
        <StackLine />
        <StreakCount>
          {props.stacksInfo[props.stacksIndex].streak}
        </StreakCount>
      </div>
    </div>

  )
}

export default Stack;
