import React, { Component } from 'react';
import Stack from './Stack';
import '../App.css'
import AnimateHeight from 'react-animate-height';


/**************
Holds the Daily Stacks View


TO DO

- 'add' button
  - addOptions
  - cancel

- display stacks and habits

- display empty stacks component if no stacks

**************/


class View_Stacks extends Component {




  displayStacks = (stacks) => {

    if (stacks.length !== 0) {

      let stacksInJSX = [];
      let visiblility = null;

      stacks.forEach((stack, i) => {

        stack.isOpen ? visiblility = "visible" : visiblility = "hidden";

        stacksInJSX.push(
          <AnimateHeight
          duration={ 500 }
          key={i}
          height={ stack.height }
          >
          <Stack
            name={stack.name}
            key={i}
            id={i}
            habits={stack.stack.habits}
            toggleStack={this.props.toggleStack}

            />
          </AnimateHeight>
        )
      });
      return stacksInJSX;
    }
    else {
      return <p>Tap + to add stacks, habits, & friends</p>
    }
  }




  //to draw from :
  addHabit = (itemPos, stackID) => {
    //recognize which was clicked
    //input module (toggle off touchspots)
    //submit to right place

    //console.log("item pos is : " + itemPos + " and stackID is : " + stackID);

    this.setState({inputtingNewHabit:true});


    //but this should only be taken once form is filled out.

    let cue = this.state.newHabit.cue;
    let action = this.state.newHabit.action;



    let newStack = this.state.stacks;
    newStack[stackID].stack.habits.splice(itemPos, 0, {cue:cue, action:action, result:"blank", fn:""});
    this.setState({stacks : newStack});
    // this.cancelNew();
  }
  logHabits = (itemPos, stackID) => {

    let h = this.state.stacks[stackID].stack.habits;

    let newH = h.map((habit, i) => {
      if (i <= itemPos){
        habit.result = "success";
      }
      else {
        habit.result ="failure";
      }
    });

    if (itemPos === h.length-1){
      console.log("SUCCESS");
    }

    this.setState({h:newH});

  }




  render() {

    /**************
    Logic

    - Check if there are any stacks available in state
      - no? emptyStacks message
      - yes? display


    - Add button clicked
      - show new stack touchPoint
      - is there

    **************/

    let stacksToDisplay = this.displayStacks(this.props.stacks);


    return (
      <>
        <div className="habitsTopBar">
          <p className="title">Dec. 6th</p>
          <button className="add"></button>
        </div>

        {stacksToDisplay}


      </>
    );
  }
}



export default View_Stacks;
