import React from 'react';
import '../App.css';
import Habit from './Habit';

/**************
Stack Component

is it bad I affect the DOM with the toggleStack() ?
**************/

function displayHabits(stack){

  if (stack.length !== 0) {
    let habitsInJSX = [];
    stack.forEach((habit, i) => {
      habitsInJSX.push(
        <Habit
          action={habit.action}
          cue={habit.cue}
          key={i}/>
      )
    });
    return habitsInJSX;
  }
  else {
    return <p>Tap + to add a habit</p>
  }
}



