import React, { Component } from 'react';
import Stack from './Stack';
import styled from 'styled-components';
import '../App.css';
import {sortableContainer} from 'react-sortable-hoc';


/**************
Holds the Daily Stacks View


TO DO

- 'add' button
  - addOptions
  - cancel

- display stacks and habits

- display empty stacks component if no stacks

**************/



const SortableContainer = sortableContainer(({children}) => {
  return <div>{children}</div>;
});


const Head = styled.div`
  position:fixed;
  top:0; left:0;
  display: flex;
  flex-direction : row;
  justify-content: flex-end;
  align-items: center;
  width:100%;
  height: 100px;

  background-color: #fff;
  z-index: 5;
`

const MainDate = styled.h3`
  font-size: 1.25rem;
  color: #4E4E4E;
  margin: 0px;
  margin-left: 1rem;
  margin-right: auto;

`

const AddNew = styled.div`
  background: #FEBE00;
  border-radius: 6px;
  height: 40px;
  width:85px;
  padding-top: 5px;
  font-family: Poppins;
  font-weight: 500;
  font-size: 1.25rem;
  color: #3D3D3D;
  text-align: center;
`

const Edit = styled(AddNew)`
  background: none;
`


class ViewStacks extends Component {

  render() {

    const {stacks} = this.props.stacks;

    let addMode = "+ Add";
    if (this.props.addModeIsActive){addMode = "Cancel"}

    return (
      <>
        <AddHabitModule />
        <Head>
          <MainDate>Dec. 6th</MainDate>
          <AddNew onClick={this.props.addMode}>
              {addMode}
          </AddNew>
          <Edit> Edit </Edit>
        </Head>
        <SortableContainer
          onSortEnd={this.props.onSortEnd}
          useDragHandle={true}
          lockAxis="y"
          >

          {stacks.map((items, index) => (
            <Stack
              stacksItems={items}
              stacksIndex={index}
              key={index}
              stacksInfo={this.props.stacksInfo}
              height={this.props.stacksInfo[index].height}
              streak={this.props.stacksInfo[index].streak}
              toggleStack={this.props.toggleStack}
              logHabit={this.props.logHabit}
              addHabit={this.props.addHabit}
              addModeIsActive={this.props.addModeIsActive}
            />
          ))}
        </SortableContainer>
    </>
    );
  }
}



export default ViewStacks;
