import React from 'react';
import styled from 'styled-components';
import '../App.css';
import {sortableElement, sortableHandle} from 'react-sortable-hoc';

const Li = styled.li`
  height:60px;
  margin-top: 25px;
  margin-bottom: 25px;
  list-style: none;
`

const Circle = styled.div`
  display:inline-block;
  margin: 20px 0px;
  height:1.625rem;
  width:1.625rem;
  border-radius:30px;
  border: solid 1px #C5C5C5;
  transition: box-shadow .3s;
  transition: background-color .2s;
`

const HabitText = styled.div`
  display: inline-block;
  margin-left: 20px;
  vertical-align: top;
`
const HabitOptions = styled.div`
  display: inline-block;
  margin-left: 20px;
  vertical-align: top;
`

const Cue = styled.div`
  font-family: Roboto;
  font-weight: 300;
  font-style: italic;
  font-size: 1.125rem;
  color: #4E4E4E;
  letter-spacing: 0;
  line-height: 30px;
`

const Action = styled.div`
  font-family: Roboto;
  font-weight: 500;
  font-size: 1.4rem;
  color: #4E4E4E;
  letter-spacing: 0;
  line-height: 30px;
`;

const DragHandle = sortableHandle((className) => <span className={className}>::</span>);
const DragHandleStyled = styled(DragHandle)`
  background-color: red;
  color:  red;
`;

const Link = ({ className, children }) => (
  <span className={className}>
    {children}
  </span>
);

const StyledLink = styled(Link)`
  color: palevioletred;
  font-weight: bold;
`;


const SortableItem = sortableElement(
  ({value}) => (
    <Li>
      <Circle />
      <HabitText>
        <Cue>{value.cue}</Cue>
        <Action>{value.action}</Action>
      </HabitText>
      <HabitOptions>
          <DragHandleStyled/>
          <Link>Unstyled</Link>
          <br />
          <StyledLink>Styled</StyledLink>
      </HabitOptions>

    </Li>
  )
);


function Habit(props){
  return (
    <div onClick={props.logHabit}>
      <SortableItem
        value={props.value}
        index={props.index}
        collection={props.collection}
        onClick={props.logHabit}
      />
    </div>
  )
}


export default Habit;
