import React, { Component } from 'react';
import './App.css';
import ViewStacks from './Components/ViewStacks';
import AnimateHeight from 'react-animate-height';
import {arrayMove} from 'react-sortable-hoc';

/**************
App.js composed of :

1) State for all aspects of app
2) App Render

Note:
array of habits (stacks[]) is separate from array of stack info (stacksInfo[])
due to how Sortable library works

**************/


class App extends Component {



  state = {

    stacks : [
      [ {action:"Meditate 15mins", cue:"7:00am"},{action:"Exercise", cue:"then"},{action:"Shower", cue:"then"}, ],
      [ {action:"Meditate 15mins", cue:"7:00am"},{action:"Exercise", cue:"then"},{action:"Shower", cue:"then"}, ],
      [ {action:"Meditate 15mins", cue:"7:00am"},{action:"Exercise", cue:"then"},{action:"Shower", cue:"then"}, ],

    ],
    stacksInfo : [
      { name : "Morning Routine", streak: 10, height : "auto", },
      { name : "After School ", streak: 4, height : "auto", },
      { name : "Night Routine", streak: 9, height : "auto", },
    ],

    makingNewHabit : false,
    inputtingNewHabit : false,
    newHabit : {
      cue:"then",
      action:"",
      itemPos:null,
      stackID:null,
    }
  };



  toggleStack = (id) => {

    console.log("stacksInfo ", this.state.stacksInfo[id]);
    console.log("stacksInfo ", id);

    let oldStack = this.state.stacksInfo[id];
    let newStack = oldStack;

    newStack.height = oldStack.height === 10 ? 'auto' : 10;

    this.setState({
      oldStack : newStack
    })
  }


  onSortEnd = ({oldIndex, newIndex, collection}) => {
    this.setState(({stacks}) => {
      const newstacks = [...stacks];

      newstacks[collection] = arrayMove(
        stacks[collection],
        oldIndex,
        newIndex,
      );
      return {stacks: newstacks};
    });
  };


  render() {
    return (
      <div className="App">

        <ViewStacks
          stacks={this.state}
          stacksInfo={this.state.stacksInfo}
          toggleStack={this.toggleStack}
          onSortEnd={this.onSortEnd}
          />

      </div>
    );
  }
}

export default App;
