import React from 'react';
import styled from 'styled-components';
import { Formik, Field, Form } from 'formik';

//Formik form taken right from https://alligator.io/react/forms-with-react-and-formik/ and modified

const Module = styled.div`
  position:absolute;
  width: 100%;
  height: 100%;
  z-index: 10;
  background-color:#fff;
  transition: right .2s ease;
  top:0;
  right: ${props => props.active ? "0" : "100%"}
`

const Cancel = styled.button`
  background: #F3F3F3;
  margin-right: 10px;
  position: relative;
  margin-left: auto;
  margin-top: 10px;
`

function AddHabitModule(props){
  return (
    <Module active={props.activeStates.addModuleIsActive}>
      <Cancel onClick={props.cancelHabitModule}> Cancel </Cancel>
      <Formik
        // Sets up our default values
        initialValues={{ email: "", password: "" }}

        // Validates our data
        validate={values => {
          const errors = {};

          if (!values.email) errors.email = "Required";

          if (
            !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)
          ) {
            errors.email = "You must supply a valid email address";
          }

          if (values.password.length < 8) {
            errors.password = "Passwords must be at least 8 characters";
          }

          if (values.email === values.password) {
            errors.password =
              "Your password shouldn't be the same as your email";
          }

          return errors;
        }}

        // Handles our submission
        onSubmit={(values, { setSubmitting }) => {
          // This is where you could wire up axios or superagent
          console.log("Submitted Values:", values);
          // Simulates the delay of a real request
          setTimeout(() => setSubmitting(false), 3 * 1000);
        }}
      >
        {props => (
          <Form>
            <label htmlFor="email">Email</label>
            <div>
              <input
                name="email"
                type="email"
                placeholder="Enter your account email"
                value={props.values.email}
                onChange={props.handleChange}
                onBlur={props.handleBlur}
                style={{
                  borderColor:
                    props.errors.email && props.touched.email && "red"
                }}
              />
              {props.errors.email && props.touched.email && (
                <div style={{ color: "red" }}>{props.errors.email}</div>
              )}
            </div>
            <label htmlFor="password">Password</label>
            <div>
              <input
                name="password"
                type="password"
                placeholder="Enter your account password"
                value={props.values.password}
                onChange={props.handleChange}
                onBlur={props.handleBlur}
                style={{
                  borderColor:
                    props.errors.password && props.touched.password && "red"
                }}
              />
              {props.errors.password && props.touched.password && (
                <div style={{ color: "red" }}>{props.errors.password}</div>
              )}
            </div>
            <input
              type="submit"
              value="Submit"
              disabled={props.isSubmitting}
            />
            &nbsp;
            <input
              type="reset"
              value="Reset"
              onClick={props.handleReset}
              disabled={!props.dirty || props.isSubmitting}
            />
          </Form>
        )}
      </Formik>

    </Module>
  )
}


export default AddHabitModule;
